import React, { useEffect, useState } from 'react';
import Navbar from './components/Navbar';
import { LineChart, Line, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';
import axios from 'axios';

const Predicts = () => {
  const [predictData, setPredictData] = useState([]);
  const [closeData, setCloseData] = useState([]);

  // Fetch predicted data
  const getPredictData = async () => {
    try {
      const res = await axios.get('http://localhost:5000/predict');
      setPredictData(res.data);
    } catch (error) {
      console.error('Error fetching predicted data:', error);
    }
  };

  // Fetch historical Close data
  const getCloseData = async () => {
    try {
      const res = await axios.get('http://localhost:5000/data/?field=Close'); // Update to your actual endpoint for historical data
      setCloseData(res.data);
    } catch (error) {
      console.error('Error fetching historical data:', error);
    }
  };

  // Combine historical and predicted data
  const combineData = () => {
    // Convert dates to comparable formats and remove potential duplicates
    const combined = closeData.map(item => ({
      Date: item.Date,
      Close: item.Close,
      isPredicted: false, // Indicate that this data is historical
    }));

    const predicted = predictData.map(item => ({
      Date: item.Date,
      Close: item.Close,
      isPredicted: true, // Indicate that this data is predicted
    }));

    return [...combined, ...predicted].sort((a, b) => new Date(a.Date) - new Date(b.Date));
  };

  // Get combined data for plotting
  const combinedData = combineData();

  useEffect(() => {
    getPredictData();
    getCloseData();
  }, []);

  return (
    <>
      <Navbar />
      <div className="w-screen h-screen grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 grid-rows-8 md:grid-rows-7 xl:grid-rows-5 auto-rows-fr gap-6 p-10 bg-gray-900 text-gray-300">
        <div className="col-span-3 row-span-3">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart data={combinedData}>
            <XAxis dataKey="Date" tickFormatter={date => new Date(date).toLocaleDateString()} />
            <YAxis />
            <Tooltip labelFormatter={label => new Date(label).toLocaleDateString()} />
            <Line type="monotone" dataKey="Close" stroke="#8884d8" dot={false} />
            <Line type="monotone" dataKey="Close" stroke="#ff7300" dot={false} isAnimationActive={false} 
                  connectNulls={true} data={combinedData.filter(item => item.isPredicted)} />
          </LineChart>
        </ResponsiveContainer>
        </div>
      </div>
    </>
  );
};

export default Predicts;
