import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { AreaChart, Area, ResponsiveContainer, XAxis, YAxis, Tooltip } from 'recharts';
import dayjs from 'dayjs';

const CloseValueLAreaChart = () => {
  const [data, setData] = useState([]);
  const [startDate, setStartDate] = useState('2008-03-19T00:00:00.000+00:00');
  const [endDate, setEndDate] = useState('2024-10-21T00:00:00.000+00:00');
  const minDate = dayjs('2008-03-19T00:00:00.000+00:00');
  const maxDate = dayjs('2024-10-21T00:00:00.000+00:00');

  const getData = async (start_date, end_date) => {
    try {
      const res = await axios.get(`http://localhost:5000/data?field=Close&start_date=${start_date}&end_date=${end_date}`);
      setData(res.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const validateDates = (start, end) => {
    const startDayjs = dayjs(start);
    const endDayjs = dayjs(end);
    
    if (endDayjs.isBefore(startDayjs)) {
      alert("End date must be greater than start date.");
      return false;
    }
    if (startDayjs.isBefore(minDate) || startDayjs.isAfter(maxDate)) {
      alert(`Start date must be between ${minDate.format('YYYY-MM-DD')} and ${maxDate.format('YYYY-MM-DD')}.`);
      return false;
    }
    if (endDayjs.isBefore(minDate) || endDayjs.isAfter(maxDate)) {
      alert(`End date must be between ${minDate.format('YYYY-MM-DD')} and ${maxDate.format('YYYY-MM-DD')}.`);
      return false;
    }
    return true;
  };

  useEffect(() => {
    if (validateDates(startDate, endDate)) {
      getData(startDate, endDate);
    }
  }, [startDate, endDate]);

  // Custom date formatting for X-axis
  const formatDate = (date) => dayjs(date).format('MM-YYYY');

  return (
    <div className="w-full h-full p-4">
      <div className="flex items-center mb-4 gap-4">
        <label>
          Start Date:
          <input 
            type="datetime-local" 
            value={dayjs(startDate).format('YYYY-MM-DDTHH:mm')} 
            onChange={(e) => setStartDate(e.target.value)} 
            className="ml-2 p-1 rounded border-2 border-gray-300 bg-gray-800 text-gray-100"
          />
        </label>
        <label>
          End Date:
          <input 
            type="datetime-local" 
            value={dayjs(endDate).format('YYYY-MM-DDTHH:mm')} 
            onChange={(e) => setEndDate(e.target.value)} 
            className="ml-2 p-1 rounded border-2 border-gray-300 bg-gray-800 text-gray-100"
          />
        </label>
      </div>
      
      <ResponsiveContainer width="100%" height="100%">
        <AreaChart data={data} margin={{ top: 10, right: 30, left: 0, bottom: 0 }}>
          <XAxis dataKey="Date" tickFormatter={formatDate} />
          <YAxis />
          <Tooltip />
          <Area type="monotone" dataKey="Close" stroke="#8884d8" fill="#8884d8" />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

export default CloseValueLAreaChart;
