import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { AreaChart, Area, ResponsiveContainer, XAxis, YAxis, Tooltip } from 'recharts';

const CloseValueLAreaChart = () => {
  const [data, setData] = useState([]);

  const getData = async (start_date = '2008-03-19', end_date = '2024-10-21') => {
    try {
      const res = await axios.get(`http://localhost:5000/data?field=Close&start_date=${start_date}&end_date=${end_date}`);
      setData(res.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const formatData = (data) => {
    return data.map((d) => {
      return {
        Date: d.Date,
        Close: d.Close.toFixed(2)
      };
    });
  }
  useEffect(() => {
    getData();
  }, []);

  return (
    <div style={{ width: '100%', height: '100%' }}>
      <ResponsiveContainer width='100%' height='100%'>
        <AreaChart
          data={data}
          margin={{ top: 10, right: 30, left: 0, bottom: 0 }}
        >
          <XAxis dataKey="Date" />
          <YAxis />
          <Tooltip />
          <Area type="monotone" dataKey="Close" stroke="#8884d8" fill="#8884d8" />
        </AreaChart>
      </ResponsiveContainer>
    </div>
  );
};

export default CloseValueLAreaChart;
